import React, {Component, useState} from 'react';
import { FaShoppingBag } from "react-icons/fa"

export default function Header() {
    let [cartOpen, setCartOpen] = useState(false)

        return (
            <header>
                <div>
                    <span className="logo">Volkswagen в Україні</span>
                    <ul className="nav">
                        <li>Про нас</li>
                        <li>Контакты</li>
                        <li>Кабінет</li>
                    </ul>
                    <FaShoppingBag onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`shop-cart-button ${cartOpen && ''}`} />
                </div>
                <div className="presentation"></div>
            </header>
        );
}
