import React, {Component, useState} from 'react';
import { FaShoppingBag } from "react-icons/fa"
import Order from "./Order";

const showOrders = (props) => {
    return (<div>
        {props.orders.map(el => (
            <Order onDelete={props.onDelete} key={el.id} item={el} />
        ))}
        <p className="summa">Сумма: {summa}</p>
    </div>)
}

const showNothing = () => {
    return (<div className="empty">
        <h2>Ви ще не обрали автомобіль</h2>
    </div>)
}

export default function Header(props) {
    let [cartOpen, setCartOpen] = useState(false)

        return (
            <header>
                <div>
                    <span className="logo">Volkswagen в Україні</span>
                    <ul className="nav">
                        <li>Про нас</li>
                        <li>Контакты</li>
                        <li>Кабінет</li>
                    </ul>
                    <FaShoppingBag onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`shop-cart-button ${cartOpen && 'active'}`} />

                    {cartOpen && (
                        <div className="shop-cart">
                            {props.orders.length > 0 ?
                            showOrders(props) : showNothing()}
                        </div>
                    )}

                </div>
                <div className="presentation"></div>
            </header>
        );
}
